&lt;script type="text/javascript"&gt;
  function googleTranslateElementInit() {
    new google.translate.TranslateElement({pageLanguage: 'es', 
    includedLanguages: 'en,fr,de,nl', 
    layout: google.translate.TranslateElement.InlineLayout.SIMPLE, 
    autoDisplay: false, 
    gaTrack: true, 
    toLanguage: 'en',
    multilanguagePage: true,
    gaId: 'UA-XXXXXXXX-X'}, 
    'google_translate_element');
  }
&lt;/script&gt;

&lt;script type="text/javascript" src="//translate.google.com/translate_a/element.js?cb=googleTranslateElementInit"&gt;&lt;/script&gt;

&lt;ul class="navbar-nav navbar-nav-scroll ms-auto"&gt;
  &lt;li class="nav-translate"&gt;
    &lt;div id="google_translate_element"&gt;&lt;/div&gt;
  &lt;/li&gt;
&lt;/ul&gt;

&lt;style&gt;
  .nav-translate {
    position: relative;
    margin-left: 10px;
  }
  #google_translate_element {
    display: flex;
    align-items: center;
  }

  #\:0\.targetLanguage &gt; span &gt; a &gt; span:nth-child(1) {
  font-size: 0;
  }
  #\:0\.targetLanguage &gt; span &gt; a &gt; span:nth-child(1)::after {
  content: "Translate";
  font-size: 14px;
}

&lt;/style&gt;

&lt;script&gt;
  document.addEventListener('DOMContentLoaded', function () {
    var navbar = document.querySelector('#navbarCollapse &gt; div');
    navbar.insertAdjacentHTML('afterbegin', '&lt;div id="google_translate_element"&gt;&lt;/div&gt;');
  });
&lt;/script&gt;
